import { Vertex } from "../functions/10-Estructures"
import { updateEvery } from "../functions/14-Global"
import { updateQubsBy } from "../functions/16-UpdateQubsBy"
import { create3 } from "../functions/17-Create"
import Two from "./2-Two"

export default function Auxiliar({sizing, setS}){

    let size= parseInt(sizing)

    var s1= Vertex(size) //Estructuras

    var u1= updateEvery(size*size, s1.rows, s1.cols, s1.boxs, s1.qubs) //Con 0s
    console.log({s1, u1})
    console.log(equal(size,s1,u1))


    function equal(size,c1_s,s1){
        for(let pp=0; pp<size;pp++){
            if(!c1_s.rows[pp].every(function(v,i) { return v === s1.rows[pp][i] } )) return false;
        }
        return true
    }

    function generate(){
        var c1_s= create3(size, s1.rows, s1.cols, s1.boxs, s1.qubs) //Con el sudoku lleno
    
        c1_s.qubs = updateQubsBy('rows', s1.rows) //Qubs actualizados
        console.log({c1_s})
    
        console.log(equal(size,c1_s,s1))
    }

    return (
        <div>
            {/* { c1_s.rows.length &&
            <Two rows={c1_s.rows} sizing={size} cols={c1_s.cols} qubs={c1_s.qubs} boxs={c1_s.boxs}/>
            } */}
            <button onClick={generate}>
                Generate
            </button>
        </div>
    )
}
