import { useState } from "react"
import { Vertex } from "../functions/10-Estructures"
import { updateEvery } from "../functions/14-Global"
import { updateQubsBy } from "../functions/16-UpdateQubsBy"
import { create3 } from "../functions/17-Create"
import Two from "./2-Two"
import '../styles/ask.css';

export default function Auxiliar({sizing}){

    let[sudoku, setSudoku] = useState(undefined)
    let[asking, setAsking] = useState(false)

    let size= parseInt(sizing)
    var s1= Vertex(size) //Estructuras
    var u1= updateEvery(size*size, s1.rows, s1.cols, s1.boxs, s1.qubs) //Con 0s

    // console.log({s1, u1})
    // console.log(equal(size,s1,u1))

    function equal(size,c1_s,s1){
        for(let pp=0; pp<size;pp++){
            if(!c1_s.rows[pp].every(function(v,i) { return v === s1.rows[pp][i] } )) return false;
        }
        return true
    }

    function ask(){
        // if(sudoku && size!==sudoku.qubs/size) return setAsking(true)
        // return generate()
        console.log(sudoku.qubs, size!==sudoku.qubs/size)
    }

    function generate(){

        var c1_s= create3(size, s1.rows, s1.cols, s1.boxs, s1.qubs) //Con el sudoku lleno
        c1_s.qubs = updateQubsBy('rows', s1.rows) //Qubs actualizados

        console.log({c1_s})
        console.log('lo que estás buscando',equal(size,c1_s,s1))

        setSudoku(c1_s)

    }

    function del(){
        setSudoku(undefined)
    }

    if(sudoku)console.log(sudoku)

    return (
        <div>{ asking &&
            <div className="ask">
                Are you sure?
            </div>}
            <div className={asking && 'imnotask'}>
                {sizing &&
                <button onClick={ask}>
                    Generate
                </button>}
                <button onClick={del}>
                    Delete
                </button>
                {sudoku &&
                <Two rows={sudoku.rows} sizing={size} cols={sudoku.cols} qubs={sudoku.qubs} boxs={sudoku.boxs}/>
                }
            </div>
        </div>
    )
}
