const updateSquare = (q, nueve, rows, cols, boxs) => { //Actualiza un qube puntual => q

    updateBox(q, nueve, boxs)
    updateCol(q, nueve, cols)
    updateRow(q, nueve, rows)

    return{
        rows,
        cols,
        boxs
    }
}

function resetSquare(q, nueve, rows, cols, boxs) { //Resetea un qube puntual => q

    qubs[q]= 0
    updateBox(q, nueve, boxs)
    updateCol(q, nueve, cols)
    updateRow(q, nueve, rows)

