function sqrt(num){

    if(Math.sqrt(num)%1===0){
        return true
    }
    if(Math.sqrt(num)%1!==0){
        return false
    }

}

function boxSize(num){

    if(sqrt(num)) return {
        width: Math.sqrt(num), 
        heigth: Math.sqrt(num)
    }

    let width = 2
    let heigth = num / width

    return {
        width, 
        heigth
    }
}

export {
    sqrt,
    boxSize,
}
